import React, { useState } from "react";
import './FenceCalculator.css';

const FenceCalculator = () => {
  const [length, setLength] = useState('');
  const [material, setMaterial] = useState('wood');
  const [difficulty, setDifficulty] = useState('normal');
  const [location, setLocation] = useState('auckland');
  const [result, setResult] = useState(null);

  const handleCalculate = () => {
    const lengthNum = parseFloat(length);
    if (isNaN(lengthNum) || lengthNum <= 0) {
      setResult("Please enter a valid length.");
      return;
    }

    // ðŸ’° Labor per meter
    const laborRates = {
      wood: 80,
      metal: 100,
      composite: 110
    };

    // ðŸ§± Material prices
    const materialPrices = {
      post: 25,
      rail: 12,
      picket: { wood: 3, metal: 5, composite: 6 },
      nail: 0.05,
      bolt: 1.5,
      cement: 13.98
    };

    // ðŸ“ Construction logic
    const posts = Math.ceil(lengthNum / 2) + 1;
    const rails = Math.ceil(lengthNum * 3 / 4);
    const pickets = Math.ceil(lengthNum / 0.105); // ~9.52 per meter
    const nails = pickets * 6;
    const bolts = posts * 6;
    const cementBags = posts * 3;

    const materialTotal =
      (posts * materialPrices.post +
        rails * materialPrices.rail +
        pickets * materialPrices.picket[material] +
        nails * materialPrices.nail +
        bolts * materialPrices.bolt +
        cementBags * materialPrices.cement) * 1.1;

    let labor = lengthNum * laborRates[material];
    if (difficulty === 'high') labor *= 1.15;
    if (location === 'remote') labor *= 1.1;

    const total = (materialTotal + labor).toFixed(2);
    setResult(`Total Estimated Cost: $${total}`);
  };

  return (
    <div className="calculator-container">
      <h2>Fence Cost Calculator</h2>
      <div className="calculator-form">
        <label>
          Fence Length (meters):
          <input
            type="number"
            value={length}
            onChange={(e) => setLength(e.target.value)}
          />
        </label>
        <label>
          Material:
          <select value={material} onChange={(e) => setMaterial(e.target.value)}>
            <option value="wood">Wood</option>
            <option value="metal">Metal</option>
            <option value="composite">Composite</option>
          </select>
        </label>
        <label>
          Difficulty:
          <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
            <option value="normal">Normal</option>
            <option value="high">High</option>
          </select>
        </label>
        <label>
          Location:
          <select value={location} onChange={(e) => setLocation(e.target.value)}>
            <option value="auckland">Auckland (within 100km)</option>
            <option value="remote">Remote (100km+)</option>
          </select>
        </label>
        <button onClick={handleCalculate}>Calculate</button>
      </div>
      {result && <div className="result">{result}</div>}
    </div>
  );
};

export default FenceCalculator;
