import React, { useState } from "react";
import './FenceCalculator.css';

const FenceCalculator = () => {
    const [length, setLength] = useState('');
    const [material, setMaterial] = useState('wood');
    const [difficulty, setDifficulty] = useState('simple');
    const [region, setRegion] = useState('Auckland');
    const [result, setResult] = useState(null);

    const calculatePrice = () => {
        const materialPrices = {
            wood: 50,
            metal: 80,
            composite: 100
        };

        const difficultyMultiplier = {
            simple: 1,
            medium: 1.2,
            hard: 1.5
        };

        const regionMultiplier = {
            Auckland: 1,
            Wellington: 1.1,
            Christchurch: 1.05
        };

        const basePrice = materialPrices[material] * parseFloat(length || 0);
        const finalPrice = basePrice * difficultyMultiplier[difficulty] * regionMultiplier[region];
        setResult(finalPrice.toFixed(2));
    };

    return (
        <div className="calculator-container">
            <h2>Fence Cost Calculator</h2>
            <div className="calculator-form">
                <label>
                    Length (meters):
                    <input type="number" value={length} onChange={(e) => setLength(e.target.value)} />
                </label>
                <label>
                    Material:
                    <select value={material} onChange={(e) => setMaterial(e.target.value)}>
                        <option value="wood">Wood</option>
                        <option value="metal">Metal</option>
                        <option value="composite">Composite</option>
                    </select>
                </label>
                <label>
                    Difficulty:
                    <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
                        <option value="simple">Simple</option>
                        <option value="medium">Medium</option>
                        <option value="hard">Hard</option>
                    </select>
                </label>
                <label>
                    Region:
                    <select value={region} onChange={(e) => setRegion(e.target.value)}>
                        <option value="Auckland">Auckland</option>
                        <option value="Wellington">Wellington</option>
                        <option value="Christchurch">Christchurch</option>
                    </select>
                </label>
                <button onClick={calculatePrice}>Calculate</button>
            </div>
            {result && <div className="result">Estimated Price: ${result}</div>}
        </div>
    );
};

export default FenceCalculator;
